import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import './App.css';
import { BrowserRouter, Route } from 'react-router-dom';
import Points from './components/points.js'
import Signup from './components/signup.js'

class App extends Component {
  state = {
    players: [{
      "name": "",
      "points": 0
    },
    {
      "name": "",
      "points": 0
    }]
  }

  handlePlayerNameChange = (value) => {
    console.log("parent", value)

  }

  addNewPlayer = () => {
    console.log("adding new player")
    var contestants = this.state.players;
    contestants.push({
      "name": "",
      "points": 0
    })
    this.setState({ players: contestants })
  }

  removePlayer = () => {
    console.log("removing player");
    var contestants = this.state.players;
    contestants.pop();
    this.setState({ players: contestants })
  }

  render() {
    return (

      <BrowserRouter>
        <div>
          <Route exact path="/" render={() =>
            <Signup
              players={this.state.players}
              handleNewPlayer={this.addNewPlayer}
              handleRemovePlayer={this.removePlayer}
              handlePlayerNameChange={this.handlePlayerNameChange}
            />
          }
          />
          <Route path="/points" component={Points} exact />
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
