import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import $ from 'jquery';

class Signup extends Component {
    state = {
        numNames : 2
    }

    handleButtonClick = () => {
        // window.location.href = "/points"
        // var signup = document.getElementById("signup");
        $("#Player"+this.state.numNames).append("<div> hi </div>")
        // $('#Player'+this.state.numNames).append("<div> hi </div>")
    }

    addTextField = () => {
        console.log();
    }
    render() {
        return (
        <div id="home">
            <div id = "signup">
                <div> <TextField label="Player1" id = "Player1"/> <br /> </div>

                <TextField label="Player2" id = "Player2"/> <br /> <br />
                <Button onClick = {this.addTextField}> Add another player. </Button> <br/>
                <Button onClick = {this.handleButtonClick}> Let's Play! </Button>
            </div>
        </div>
        )
    }
}

export default Signup;
