import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import $ from 'jquery';

class Signup extends React.Component {
    state = {
        numNames : 1,
        players : [{label: 0, name: ""}, {label: 1, name: ""}],
        placeholder : ""
    }

    componentDidMount() {
        console.log("mounted")
    }

    handleTextFieldChange = (e) => {
        console.log(e.target.value);
        // console.log(e.target.value);
    }
    addTextField = () => {
        console.log("clicked on button")
        this.state.players.push({label: ++this.state.numNames, name: ""});
        console.log(this.state.players);
        this.setState({placeholder: ""});
    }

    removeTextField = () => {
        console.log("removing")
        this.state.players.pop();
        this.state.numNames--;
        this.setState({placeholder: ""});
    }

    handleButtonClick = () => {
        console.log();
    }


    render() {
        let { players } = this.state;
        const { value } = this.state;
        return (
        <div id="home">
            <div id = "logo">
                <h1> catch these hands </h1> <br/>
                a point counter for nertz, speed uno, or anything else that requires keeping track of points
                <div id = "credit"> created by jon wong because he was bored. <br/> and excel is too inefficient </div>
            </div>
            <div id = "signup">
                {players.map((v) => {
                    return <div id = {v.label}><TextField value = {value} label = "Player" onChange={() => {
                        console.log("editing: " + v.label)
                        // console.log(event.target.value);
                        // this.setState({players[v.label].name: })
                    }} /> </div>
                })}
                <br/>
                <Button onClick = {this.addTextField}> Add another player. </Button> <br/>
                <Button onClick = {this.removeTextField}> Remove player. </Button> <br/>
                <Button onClick = {this.handleButtonClick}> Let's Play! </Button>
            </div>
        </div>
        )
    }
}

export default Signup;
