import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import $ from 'jquery';

class Signup extends React.Component {
    state = {
        numNames : 2,
        players : [{label: "Player", name: "" }, {}],
        placeholder : ""
    }

    componentDidMount() {
        console.log("mounted")
    }

    addTextField = () => {
        console.log("clicked on button")
        this.state.players.push("Player");
        console.log(this.state.players);
        this.setState({placeholder: ""});
    }

    removeTextField = () => {
        console.log("removing")
        this.state.players.pop();
        this.setState({placeholder: ""});
    }

    handleButtonClick = () => {
        console.log();
    }


    render() {
        let { players } = this.state;
        return (
        <div id="home">
            <div id = "logo">
                <h1> catch these hands </h1> <br/>
                a point counter for nertz, speed uno, or anything else that requires keeping track of points
                <div id = "credit"> created by jon wong because he was bored. <br/> and excel is too inefficient </div>
            </div>
            <div id = "signup">
                {players.map((v) => {
                    return <div><TextField label = {v.name} /> </div>
                })}
                <br />

                <Button onClick = {this.addTextField}> Add another player. </Button> <br/>
                <Button onClick = {this.removeTextField}> Remove player. </Button> <br/>
                <Button onClick = {this.handleButtonClick}> Let's Play! </Button>
            </div>
        </div>
        )
    }
}

export default Signup;
