import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import $ from 'jquery';

class Signup extends React.Component {
    state = {
        numNames : 2,
        players : ["Player1", "Player2"],
        placeholder : ""
    }

    componentDidMount() {
        console.log("mounted")
        $("#signup").after(<div id = "foobar"> </div>)
    }

    createTextField = () => {
        return(<div> <h1> foobar </h1></div>)
        // return(<div> <TextField label="loren ipsum"/> </div>)
    }

    handleButtonClick = () => {
        console.log("clicked on button")
        this.state.players.push("NEWPLAYER");
        this.state.numNames++;
        console.log(this.state.players);
        this.setState({placeholder: ""});
        // this.state;
        // console.log(this.state.textBoxes);
        // $("#signup").after(<TextField> sda </TextField>)
        // this.setState({numNames: 99});
        // this.forceUpdate();
        // window.location.href = "/points"
        // var signup = document.getElementById("signup");
        // $('#Player'+this.state.numNames).append("<div> hi </div>")
    }

    addTextField = () => {
        console.log();
    }
    render() {
        const { textBoxes } = this.state;
        let { players } = this.state;
        return (
        <div id="home">
            <div id = "signup">
                {/* <div id = "Player1"> <TextField label="Player1" id = "Player1"/> <br /> </div>
                <div id = "Player2"> <TextField label="Player2" id = "Player2"/> <br /> </div> */}
                {/* {this.state.textBoxes} */}
                {/* {textBoxes} */}
                {players.map((v) => {
                    return <div><TextField label = {v} /> </div>
                })}
                {this.state.numNames} <br />
                {/* <Button onClick = {this.addTextField}> Add another player. </Button> <br/> */}
                <Button onClick = {this.handleButtonClick}> Let's Play! </Button>
            </div>
        </div>
        )
    }
}

export default Signup;
