import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import $ from 'jquery';

class Signup extends React.Component {
    state = {
        numNames : 1,
        players : [{label: 0, value: ""}, {label: 1, value: ""}],
        changeMeToReloadDOM : "",
    }

    handleTextFieldChange = (textboxID, e) => {
        // this.setState({players[textboxID].value : e.target.value})
        console.log(textboxID, e.target.value);
        // console.log(this.state.playe)
        // console.log(this.state.players[textboxID].value);
        this.state.players[textboxID].value = e.target.value;
        this.setState({changeMeToReloadDOM: ""});
    }


    addTextField = () => {
        console.log("clicked on button")
        this.state.players.push({label: ++this.state.numNames, value: ""});
        console.log(this.state.players);
        this.setState({changeMeToReloadDOM: ""});
    }

    removeTextField = () => {
        console.log("removing")
        this.state.players.pop();
        this.state.numNames--;
        this.setState({changeMeToReloadDOM: ""});
    }

    handleLetsPlay = () => {
        this.state.players.forEach((s) => {
            console.log(s.value);
        })
    }


    render() {
        let { players } = this.state;
        const { value } = this.state;
        return (
        <div id="home">
            <div id = "logo">
                <h1> count these hands. </h1> <br/>
                a point counter for nertz, speed uno, or anything else that requires keeping track of points
                <div id = "credit"> created by jon wong because he was bored. </div>
            </div>
            <div id = "signup">
                {players.map((v) => {
                    return <div id = {v.label}><TextField value = {players[v.label].value} label = "Player" onChange={
                        this.handleTextFieldChange.bind(this, v.label)
                    //     () => {
                    //     console.log("editing: " + v.label)
                    //     // console.log(event.target.value);
                    //     // this.setState({players[v.label].name: })
                    // }

                } /> </div>
                })}
                <br/>
                <Button onClick = {this.addTextField}> Add another player. </Button> <br/>
                <Button onClick = {this.removeTextField}> Remove player. </Button> <br/>
                <Button onClick = {this.handleLetsPlay}> Let's Play! </Button>
            </div>
        </div>
        )
    }
}

export default Signup;
